import React from "react";
import s from "./style.module.css";

const Layout = (props) => {
  const style = {}

  // style={
  //   props.colorBg
  //     ? { backgroundColor: props.colorBg }
  //     : {
  //         // backgroundImage: props.urlBg,
  //         if (props.urlBg) {

  //         }
  //       }
  // }

  return (
    <section className={s.root} id={props.id} style={{backgroundImage: props.urlBg ? `url(${props.urlBg})` : 'none', backgroundColor: props.color ? props.color : 'none'}}>
      <div
        className="wrapper"


      >
        <article>
          <div className={s.title}>
            <h3>{props.title}</h3>
            <span className={s.separator}></span>
          </div>
          <div className={`${s.desc} ${s.full}`}>
            <p>{props.desc}</p>
          </div>
        </article>
      </div>
    </section>
  );
};

export default Layout;
